mapper.insert:
     
/* member.insertMember */
  insert into member
 (
   seq
  ,mem_id
  , mem_pass
  , mem_name
  , mem_mail
  , retire_yn
  , regdate
  )
   values
   (
   MEMBER_SEQ.NEXTVAL
   , #{mem_id} 
   ,#{mem_pass}
   ,#{mem_name}
   ,#{mem_mail}
   , 'N'
   ,SYSDATE
   )
